Test generators (handy)
import numpy as np
import pytest

# contents of add.py
def add2(x, y):
    return x + y

class Test(object):
@pytest.mark.parametrize('exp, x, y', [
    (4, 2, 2),
    (5, -5, 10),
    (42, 40, 2),
    (-128, 0, -128),
])
def test_add2(self, x, y, exp):
    obs = add2(x, y)
    assert pytest.approx(obs) == exp

